import React from 'react';

class Article extends React.Component{
    render(){
        return(
            <section>
                <h2>{this.props.title}</h2>
                <p><em>Escrito por: {this.props.author}</em></p>
                <Box>{this.props.date}</Box>
                <article>
                    {this.props.children}
                </article>
            </section>
        )
    }
}

export default class Box extends React.Component{
    /*constructor(){
