import React from 'react';


class FechExample extends React.Component{
    state = {bpi: {}}
    constructor(){
        super();
    }

    componentDidMount(){

        fetch('https://api.coindesk.com/v1/bpi/currentprice.json')
        .then(res => res.json())
        .then(data => {
            const {bpi} = data;
            this.setState({bpi})
        })
    }
    render(){
        return(
            <div>
                <h1>Example</h1>
            </div>
        )
    }
}

export default FechExample
