import React from 'react';


class FechExample extends React.Component{
    state = {bpi: {}}

    componentDidMount(){

        fetch('https://api.coindesk.com/v1/bpi/currentprice.json')
        .then(res => res.json())
        .then(data => {
            const {bpi} = data;
            this.setState({bpi})
        })
    }

    _renderCurrencies() {
        const {bpi} = this.state;

        return Object.keys(bpi)
                    .map(currency => 
                    { 
                        return(
                            <div key ={currency}>
                                1 BCT is {bpi[currency].rate}
                                <span>{currency}</span>
                            </div> 
                        )
                    })
    }

    render(){
        return(
            <div>
                <h1>Example</h1>
                {this._renderCurrencies()}
            </div>
        )
    }
}

export default FechExample
