import React, {Component} from 'react'

class LoginButton extends Component{
    render(){
        return <button>Login Button</button>
    }
}

class LogoutButton extends Component{
    render(){
        return (
            <div>
                <p>Bienvenido Usuario</p>
                <button>Cerrar Sesion</button>
            </div>
        )
    }
}

