        return (
            <div>
                <h4>Condional Rendering</h4>
                {/*useConditionalRendering(this.state.mostrarA)*/
                 this.state.isUserLog 
                 ? <LogoutButton/>
                 : <LoginButton/>
                }
            </div>
        );
    }
}
